void set_refresh_rate(int timing, struct video_canvas_s *canvas) {
  if (timing == MACHINE_TIMING_NTSC_HDMI ||
      timing == MACHINE_TIMING_NTSC_COMPOSITE ||
      timing == MACHINE_TIMING_NTSC_CUSTOM) {
    canvas->refreshrate = PLUS4_NTSC_RFSH_PER_SEC;
  } else {
    canvas->refreshrate = PLUS4_PAL_RFSH_PER_SEC;
  }
}

void set_video_font(void) {
  int i;
  video_font = plus4memrom_kernal_rom + 0x1400;
  raw_video_font = plus4memrom_kernal_rom + 0x1000;
  for (i = 0; i < 256; ++i) {
    video_font_translate[i] = 8 * char_to_screen[i];
  }
}

unsigned int *raspi_get_palette(int index) {
  switch (index) {
  case 0:
    return pal_color_palette;
    break;
  case 1:
    return ntsc_color_palette;
    break;
  default:
    return NULL;
  }
}
