void set_refresh_rate(int timing, struct video_canvas_s *canvas) {
  if (timing == MACHINE_TIMING_NTSC_HDMI ||
      timing == MACHINE_TIMING_NTSC_COMPOSITE ||
      timing == MACHINE_TIMING_NTSC_CUSTOM) {
    canvas->refreshrate = VIC20_NTSC_RFSH_PER_SEC;
  } else {
    canvas->refreshrate = VIC20_PAL_RFSH_PER_SEC;
  }
}

void set_video_font(void) {
  int i;
  video_font = (uint8_t *)&font8x8_basic;
  for (i = 0; i < 256; ++i) {
    video_font_translate[i] = (8 * (i & 0x7f));
  }
}


unsigned int *raspi_get_palette(int index) {
  switch (index) {
  case 0:
    return vice_color_palette;
    break;
  case 1:
    return colodore_color_palette;
    break;
  case 2:
    return mike_ntsc_color_palette;
    break;
  case 3:
    return mike_pal_color_palette;
    break;
  default:
    return NULL;
  }
}
